import React, {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom';
import axios from "axios";
import Demot from './Demot';

const SinglePageDemot = () => {
    const {id} = useParams();
    const [demot, setDemot] = useState("")
 

    useEffect(() => {
        async function getData(){
            axios.get('http://127.0.0.1:8000/api/' + id +'/', { validateStatus: false })
            .then(function (response) {
                    setDemot(response?.data) 
            }).catch(e => {
                if (e.response.status === 404){
                    console.log("ERROR!")
                }
            })
        }
        getData();
        
      }, [id])

    return (
        <div>
            <Demot
              key= {demot.id} 
              id = {demot.id}
              whole = {true}
              title={demot.title}
              subtitle={demot.subtitle}  
              image={demot.image}
              upvote={demot.upvote}
              downvote={demot.downvote}
              ips={demot.ips}
              />
        </div>
    )
}

export default SinglePageDemot
