import React, {useState} from "react";
import Modal from 'react-modal';
import axios from "axios";

function Navbar(props){

    const [modalIsOpen, setModalIsOpen] = useState(false)
    const [title, setTitle] = useState('')
    const [subtitle, setSubtitle] = useState('')
    const [image, setImage] = useState([])


    function addModal(){
        const data = {"title": title, "subtitle": subtitle}
        setTitle('')
        setSubtitle('')
        axios.post("/api/", data)
        setModalIsOpen(false)
        setTimeout(props.refresh, 1000)
    }

    function openAddDemotModal(){
        setModalIsOpen(!modalIsOpen)
    }

    function handleTitleChange(event){
        setTitle(event.target.value)
    }
    function handleSubtitleChange(event){
        setSubtitle(event.target.value)
    }
    function handleImageChange(event){
        setImage(event.target.file[0])
    }

    return (<nav className="navbar navbar-expand-lg navbar-dark bg-dark justify-content-center">
    <a className="navbar-brand abs" href="#">DEMOT</a>
    <Modal centered className="addDemotModal" isOpen={modalIsOpen}>
    <h1>Add demot</h1>
    <input onChange={handleTitleChange} value={title} id="title" placeholder="Write your title"></input>
    <input onChange={handleSubtitleChange} value={subtitle} id="subtitle" placeholder="Write your subtitle"></input>
    <input onChange={handleImageChange} type="file" id="img" name="img" accept="image/*" />
    <button onClick={() => {addModal()}}>ADD</button>
    <button onClick={() => {openAddDemotModal()}}>CLOSE</button>
    </Modal>
    <div className="collapse navbar-collapse flex-grow-1 text-right" id="myNavbar">
        <ul className="navbar-nav ml-auto flex-nowrap">
            <li className="nav-item">
                <button onClick={() => {openAddDemotModal()}} className="nav-link m-2 menu-item addButton"></button>
            </li>
            <li className="nav-item">
                <a href="#" className="nav-link m-2 menu-item">Logout</a>
            </li>
        </ul>
    </div>
  </nav>)
}

export default Navbar
