import React, {useState} from "react";
import axios from "axios";
import AddDemotModal from "./AddDemotModal";


function Navbar(props){

    const [modalIsOpen, setModalIsOpen] = useState(false)

    function registerModal(){
        var formData = new FormData();
        formData.append('username', "ABCDEFGH")
        formData.append('email', "asadssa@gmail.com")
        formData.append('password', "SAsaAdsd2!@12ab")
        axios.post("http://127.0.0.1:8000/register/", formData)
        .then(function (response) {
            localStorage.setItem('LoginToken', response.data.token);
        })
        props.refresh()
    }

    function logout(){
        localStorage.removeItem('LoginToken')
        props.refresh()
    }

    function login(){
        var formData = new FormData();
        formData.append('username', "ABCDEFGH")
        formData.append('password', "SAsaAdsd2!@12ab")
        axios.post("http://127.0.0.1:8000/login/", formData)
        .then(function (response) {
            console.log(response.data)
            localStorage.setItem('LoginToken', response.data.token);
        })
        props.refresh()
    }

    function openAddDemotModal(){
        setModalIsOpen(!modalIsOpen)
    }

    function LoginOrLogout(){
        let loginToken = localStorage.getItem('LoginToken')
        if (loginToken == null){
            return <div>
            <li className="nav-item login-buttons">
                <div onClick={() => {registerModal()}}>Register</div>
            </li>
            <li className="nav-item login-buttons">
                <div onClick={() => {login()}}>Login</div>
            </li>
            </div>
        }
        else{
            return <div>
            <li className="nav-item login-buttons">
                <div>Hello {props.username}</div>
            </li>
            <li className="nav-item login-buttons">
                <div onClick={() => {logout()}}>Logout</div>
            </li>
            </div>
        }
    }


    return (<nav className="navbar navbar-expand navbar-dark bg-dark justify-content-center">
    <a className="navbar-brand abs" href="/">DEMOT</a>

    <AddDemotModal refresh={props.refresh} isOpen={modalIsOpen} openAddDemotModal={openAddDemotModal}
        setCurrentPage={props.setCurrentPage}
    />

    <div className="collapse navbar-collapse flex-grow-1 text-right" id="myNavbar">
        <ul className="navbar-nav">
        <LoginOrLogout />
            <li className="nav-item">
                <button onClick={() => {openAddDemotModal()}} className="nav-link m-2 menu-item addButton"></button>
            </li>

        </ul>
    </div>
  </nav>)
}

export default Navbar
