import React, {useState} from "react";
import axios from "axios";
import RegisterModal from "./RegisterModal";

function LoginOrLogout(props) {
    // function registerModal(){
    //     var formData = new FormData();
    //     formData.append('username', "ABCDEFGH")
    //     formData.append('email', "asadssa@gmail.com")
    //     formData.append('password', "SAsaAdsd2!@12ab")
    //     axios.post("http://127.0.0.1:8000/register/", formData)
    //     .then(function (response) {
    //         localStorage.setItem('LoginToken', response.data.token);
    //     })
    //     props.refresh()
    // }

    const [registerModalIsOpen, setRegisterModalIsOpen] = useState(false)

    function openRegisterModal(){
        setRegisterModalIsOpen(!registerModalIsOpen)
    }

    function logout(){
        localStorage.removeItem('LoginToken')
        props.refresh()
    }

    function login(){
        var formData = new FormData();
        formData.append('username', "ABCDEFGH")
        formData.append('password', "SAsaAdsd2!@12ab")
        axios.post("http://127.0.0.1:8000/login/", formData)
        .then(function (response) {
            console.log(response.data)
            localStorage.setItem('LoginToken', response.data.token);
        })
        props.refresh()
    }


    let loginToken = localStorage.getItem('LoginToken')
        if (loginToken == null){
            return <div>
            <li className="nav-item login-buttons">
                <RegisterModal isOpen={registerModalIsOpen}/>
                <div onClick={() => {openRegisterModal()}}>Register</div>
            </li>
            <li className="nav-item login-buttons">
                <div onClick={() => {login()}}>Login</div>
            </li>
            </div>
        }
        else{
            return <div>
            <li className="nav-item login-buttons">
                <div>Hello {props.username}</div>
            </li>
            <li className="nav-item login-buttons">
                <div onClick={() => {logout()}}>Logout</div>
            </li>
            </div>
        }
    }


export default LoginOrLogout
