import React, {useState, useEffect} from "react";
import axios from "axios";

function Demot(props){

    var [totalVote, setTotalVote] = useState(props.upvote - props.downvote)
    var [upVote, setUpVote] = useState(props.upvote)
    var [downVote, setDownVote] = useState(props.downvote)
    const [ip, setIP] = useState('');

    const getIPData = async () => {
        const res = await axios.get('https://geolocation-db.com/json/')
        console.log(res.data.IPv4);
        setIP(res.data.IPv4)
      }

      useEffect( () => {
        //passing getData method to the lifecycle method
        getIPData()
        console.log(ip)
    
      }, [])

    

    function voteUp(){
            setUpVote(upVote += 1)
            axios.put('/api/' + props.id + '/', {title: props.title, upvote: upVote, ip: ip})
        };

    function voteDown(){
        setDownVote(downVote += 1)
        axios.put('/api/' + props.id + '/', {title: props.title, downvote: downVote, ip: ip})
    }

    

    function ButtonPlus(){

        return (<button onClick={() =>{
            voteUp()
            setTotalVote(totalVote +=1)
        }} className="plus">+</button>)
    }

    function ButtonMinus(){
        return (<button onClick={() =>{
            voteDown()
            setTotalVote(totalVote -=1)}}
            className="minus">-</button>)
    }

    return <div className="whole-demot">
    <div className="demot">
        <img className="demot-img" src={props.image} /><h2>{props.title}</h2><h3>{props.subtitle}</h3>
    </div><ButtonPlus /><ButtonMinus /><p>Total: {totalVote}</p>
    </div>
}

export default Demot
