import React, {useState, useEffect} from "react";
import axios from "axios";

function Demot(props){

    var [totalVote, setTotalVote] = useState(props.demot.upvote - props.demot.downvote)
    var [upVote, setUpVote] = useState(props.demot.upvote)
    var [downVote, setDownVote] = useState(props.demot.downvote)
    const [ip, setIP] = useState('');
    const [isAdded, setIsAdded] = useState(false)

    const getIPData = async () => {
        const res = await axios.get('https://geolocation-db.com/json/')
        console.log(res.data.IPv4);
        setIP(res.data.IPv4)
      }

      useEffect( () => {
        getIPData()
    
      }, [])
      var ip_to_add = props.demot.ips + ", " + ip

    function voteUp(){
        if (isAdded === false){
            if (props.demot.ips == null || !props.demot.ips.includes(ip)){
                setIsAdded(true)
                setUpVote(upVote += 1)   
                axios.put('/api/' + props.demot.id + '/', {title: props.demot.title, upvote: upVote, ips: ip_to_add})
                setTotalVote(totalVote +=1)
            }
            else{
                alert("You already voted!")
            }
        }
        else {
            alert("You can vote only once!")
        }
        };

    function voteDown(){
        if (isAdded === false){
            if (props.demot.ips == null || !props.demot.ips.includes(ip)){
                setIsAdded(true)
                setDownVote(downVote += 1)
                axios.put('/api/' + props.demot.id + '/', {title: props.demot.title, downvote: downVote, ips: ip_to_add})
                setTotalVote(totalVote -=1)
            }
            else{
                alert("You already voted!")
            }
        }
        else {
            alert("You can vote only once!")
        }
    }

    

    function ButtonPlus(){

        return (<button onClick={() =>{
            voteUp()
        }} className="plus buttonMark">+</button>)
    }

    function ButtonMinus(){
        return (<button onClick={() =>{
            voteDown()
}}
            className="minus buttonMark">-</button>)
    }

    if (props.demot.whole === true){
        return <div className="whole-demot">
                <div className="demot">
                    <img alt="demot" className="demot-img" src={props.demot.image} /><h2>{props.demot.title}</h2><h3>{props.demot.subtitle}</h3>
                </div><ButtonPlus /><ButtonMinus /><p>Total: {totalVote}</p>
                </div>
            }
    else if (props.demot.whole === false){
        return <div className="demotMini">
                <div className="demot-small">
                    <img alt="demot" className="demot-small-img" src={props.demot.image} /><h4>{props.demot.title}</h4><h6>{props.demot.subtitle}</h6>
                </div>
                </div>
    }
}

export default Demot
